INSERT INTO `tourist_spot_instructions` (`id`, `tourist_spot_id`, `title`, `description`, `created_at`, `updated_at`) VALUES
(1, 4, 'কিভাবে যাওয়া যায়', '<h4 style=\"box-sizing: inherit; margin-bottom: 5px; border: 0px; vertical-align: baseline; font-family: Poppins, Arial, sans-serif; color: rgb(22, 22, 22); user-select: initial !important;\"><span>﻿</span><span style=\"font-size: 14px;\">﻿</span><strong style=\"box-sizing: inherit; border: 0px; vertical-align: baseline; font-family: inherit; user-select: initial !important;\"><span style=\"font-size: 14px;\">ঢাকা থেকে টেকনাফ \"</span></strong></h4><p><span style=\"color: rgb(51, 51, 51); font-family: Merriweather, Georgia, serif; font-size: 14px;\">ঢাকা থেকে বাসে করে সরাসরি টেকনাফে যেতে পারবেন। ঢাকার ফকিরাপুল ও সায়েদাবাদ থেকে শ্যামলী, সেন্টমার্টিন পরিবহন, ঈগল, এস আলম, মডার্ন লাইন, গ্রীন লাইন ইত্যাদি বাস সরাসরি টেকনাফ যায়। ১০-১২ ঘন্টার এই ভ্রমণ ভাড়া বাস ও ক্লাস অনুযায়ী সাধারণত ৯০০ থেকে ২০০০ টাকার মধ্যে হয়ে থাকে। যদি শুধু সেন্টমার্টিন যেতে চান তাহলে ঢাকা থেকে সরাসরি বাসে টেকনাফ চলে যাওয়াটাই সবচেয়ে ভালো। রাত ৮-১০ টার মধ্যে বাসে রওনা দিয়ে সকাল ৮টার মধ্যে টেকনাফ পৌঁছে যায়।</span></p><p><span style=\"color: rgb(51, 51, 51); font-family: Merriweather, Georgia, serif; font-size: 16px;\"><br></span></p><h4 style=\"box-sizing: inherit; margin-bottom: 5px; border: 0px; vertical-align: baseline; font-family: Poppins, Arial, sans-serif; color: rgb(22, 22, 22); user-select: initial !important;\"><strong style=\"box-sizing: inherit; border: 0px; vertical-align: baseline; font-family: inherit; user-select: initial !important;\"><span style=\"font-size: 14px;\">ঢাকা থেকে কক্সবাজার হয়ে টেকনাফ&nbsp;ঃ</span></strong></h4><p style=\"box-sizing: inherit; margin-bottom: 26px; border: 0px; vertical-align: baseline; font-size: 16px; font-family: Merriweather, Georgia, serif; color: rgb(51, 51, 51); user-select: initial !important;\"><span style=\"font-size: 14px;\">ঢাকা থেকে প্রথমে কক্সবাজার এসে তারপর কক্সবাজার থেকে টেকনাফ যেতে পারবেন। ঢাকা থেকে প্রতিদিনই গ্রীন লাইন, সোহাগ, টিআর ট্রাভেলস, শ্যামলী, হানিফ, সৌদিয়া, ঈগল, এস আলম, সিল্ক লাইন, সেন্টমার্টিন ইত্যাদি অনেক বাস কক্সবাজারের উদ্দেশ্যে ছেড়ে আসে, বাস ভেদে ভাড়া সাধারণত ১০০০ টাকা থেকে ২৫০০ টাকার মধ্যে। এছাড়াও ঢাকা থেকে বিমানে সরাসরি কক্সবাজার যাওয়া যায়।</span></p><p style=\"box-sizing: inherit; margin-bottom: 26px; border: 0px; vertical-align: baseline; font-size: 16px; font-family: Merriweather, Georgia, serif; color: rgb(51, 51, 51); user-select: initial !important;\"><strong style=\"box-sizing: inherit; border: 0px; vertical-align: baseline; user-select: initial !important;\"><span style=\"font-size: 14px;\">কক্সবাজার থেকে টেকনাফ :</span></strong><span style=\"font-size: 14px;\">&nbsp;কক্স বাজার থেকে লোকাল বাস/মাইক্রো/জিপ/সিএনজি ভাড়া করে টেকনাফ যাওয়া যায়। কক্সবাজার হতে টেকনাফ যাওয়ার বাস ভাড়া ১৫০ টাকা ও সিএনজি ভাড়া জনপ্রতি ২৫০ টাকা। টেকনাফ যেতে অবস্থা ভেদে সময় লাগে প্রায় দুই ঘন্টা। আর সকালের জাহাজ ধরতে চাইলে কক্সবাজার থেকে ভোরে (৬টার মধ্যে) টেকনাফের উদ্দেশ্যে রওনা দিতে হবে ।</span></p><p style=\"box-sizing: inherit; margin-bottom: 26px; border: 0px; vertical-align: baseline; font-size: 16px; font-family: Merriweather, Georgia, serif; color: rgb(51, 51, 51); user-select: initial !important;\"><strong style=\"color: rgb(22, 22, 22); font-family: inherit; font-size: 18px; box-sizing: inherit; border: 0px; vertical-align: baseline; user-select: initial !important;\"><span style=\"font-size: 14px;\">চট্টগ্রাম থেকে টেকনাফ ঃ&nbsp;</span></strong><span style=\"font-size: 14px;\">চট্টগ্রামের সিনেমা প্যালেস থেকে এস আলম এবং সৌদিয়া বাস রাত ১২ টায় টেকনাফের উদ্দেশ্যে ছেড়ে যায়। এছা</span>ড়া জিএসই গরিবুল্লাশাহ মাজার ও দামপাড়া থেকেও কিছু বাস চট্টগ্রাম-টেকনাফ রুটে চলাচল করে।</p><h4 style=\"box-sizing: inherit; margin-bottom: 5px; border: 0px; vertical-align: baseline; font-family: Poppins, Arial, sans-serif; color: rgb(22, 22, 22); user-select: initial !important;\"><strong style=\"box-sizing: inherit; border: 0px; vertical-align: baseline; font-family: inherit; user-select: initial !important;\"><span style=\"font-size: 14px;\">টেকনাফ থেকে সেন্টমার্টিন&nbsp;ঃ</span></strong></h4><p style=\"box-sizing: inherit; margin-bottom: 26px; border: 0px; vertical-align: baseline; font-size: 16px; font-family: Merriweather, Georgia, serif; color: rgb(51, 51, 51); user-select: initial !important;\"><span style=\"font-size: 14px;\">নভেম্বর-মার্চ মাস পর্যন্ত টেকনাফ থেকে সেন্টমার্টিনে প্রতিদিন সকাল থেকে আসা-যাওয়া করে কেয়ারী সিন্দাবাদ, কেয়ারি ক্রুজ, এম ভি ফারহান, আটলান্টিক ইত্যাদি জাহাজ। জাহাজে করে টেকনাফ থেকে সেন্টমার্টিন যেতে সময় লাগে দুই ঘণ্টা থেকে আড়াই ঘন্টা। জাহাজের শ্রেনীভেদে যাওয়া ও আসার টিকেট ভাড়া ৬৫০-১৫০০ টাকা। জেটি ঘাট থেকে প্রতিদিন জাহাজগুলো সকাল ৯.০০-৯.৩০ মিনিটে সেন্টমার্টিনের উদ্দেশ্যে ছেড়ে যায় এবং সেন্টমার্টিন থেকে ফেরত আসে বিকাল ৩.০০-৩.৩০ মিনিটে। তাই সময়ের আগে জেটি ঘাটে উপস্থিত না হতে পারলে জাহাজ মিস হবার সম্ভাবনা বাড়ে। আর এমন ক্ষেত্রে ট্রলারে করে ফেরা ছাড়া উপায় নেই যা অনেকটা বিপদজনক। সেন্টমার্টিন যাওয়ার শীপের টিকেট সাধাণত যাওয়া ও আসা সহ হয়ে থাকে। টিকেট করার সময় কবে ফিরবেন তা উল্লেখ করতে হবে</span></p><p style=\"box-sizing: inherit; margin-bottom: 26px; border: 0px; vertical-align: baseline; font-size: 16px; font-family: Merriweather, Georgia, serif; color: rgb(51, 51, 51); user-select: initial !important;\"><span style=\"font-size: 14px;\"><br></span></p><p style=\"box-sizing: inherit; margin-bottom: 26px; border: 0px; vertical-align: baseline; font-size: 16px; font-family: Merriweather, Georgia, serif; color: rgb(51, 51, 51); user-select: initial !important;\"><span style=\"font-size: 14px;\"><br></span></p><p><span style=\"font-size: 14px;\">﻿</span><br></p>', '2021-03-24 17:47:01', '2021-03-24 17:47:01');
